function isNumeroBonito($texto, $alfabeto, $tamanho_num_bonito, $num_divisor){
    $dados = explode(" ", $texto);
    $numerosBunitos = [];
    foreach ($dados as $dado) {
        $numero = tradutorAlfabetico($alfabeto ,$dado);
        if ($numero >= $tamanho_num_bonito && 0 == ($numero % $num_divisor)) {
            $key = array_search($dado, $numerosBunitos);
            if ($key === false) {
                array_push($numerosBunitos, $dado);
            }
        }
    }
    print_r(count($numerosBunitos));
}

function montarArrayAlfabeto($letra, $letra_traducao){
    if (count($letra) != count($letra_traducao)) {
        echo "Array de Letras tem tamanho diferente do letra_traducao";
        return;
    }
    $alfabeto = array();
    foreach ($letra as $key => $value) {
        $alfabeto[$value] = $letra_traducao[$key];
    }
    return $alfabeto;
}

function tradutorAlfabetico(&$alfabeto,$palavra){
    $cont = 0;
    $soma = 0;
    $base = 1;
    while (strlen($palavra) > $cont){
        foreach (array_keys($alfabeto) as $letra) {
            if ($letra == $palavra[$cont]) {
                if ($cont <= 0){
                    $soma = $alfabeto[$letra];
                }else {
                    $base = $base * 20;
                    $soma = $soma + ($alfabeto[$letra] * $base) ;
                }
            }
        }
        $cont++;
    }
    return $soma;
}

